import React,{useState, useEffect} from 'react';
import { Formik, Form, Field, ErrorMessage } from 'formik';
import * as yup from 'yup';
import Axios from 'axios';

function NewDebit() {
    const [amount, setAmount] = useState()
    const [formData, setformData] = useState()
    const [credit,setCredit] = useState([])

    console.log(amount)

    useEffect(() => {      
      if(formData){
          Axios.post("http://localhsot:2000/debit",formData)
          .then(res=>{
              console.log(res.data)
          })
          .catch(err=>{
              console.log(err)
          })

          setTimeout(get,1000)
      }
    })

    useEffect(() => {      
        Axios.get("http://localhost:2000/credit")
        .then(res=>{
            console.log(res.data) 
            setCredit(res.data)           
        })
        .catch(err=>{
            console.log(err)
        })
      },[])



    const get=()=>{
        Axios.get("http://localhost:2000/debit")
        .then(res=>{
            console.log(res.data)

        })
        .catch(err=>{
            console.log(err)
        })
    }



    const initialValue = {
        credit: "",
        reason: "",
        amount: ""
    }
    const submit = (values,props) => {
        console.log(values)

        setformData(values)
        props.resetForm()

    }
    const validationSchema = yup.object({
        select: yup.string().required("Plese Select Reason"),
        reason: yup.string().required("Reason Required"),
        amount: yup.string().required("Amount Required")
    })



    return (
        <div className="mt-3">
            <Formik
                initialValues={initialValue}
                onSubmit={submit}
                validationSchema={validationSchema}
            >
                <Form>
                    <div>
                        <Field
                            as="select"
                            name="credit"
                            className="form-control mt-2"
                            placeholder="Enter Reason for Debit"

                        //value={reason}                                                                               
                        >
                            <option value="" amount="">Select From</option>
                            {
                                credit.map((option, index) => (
                                    <option key={index} value={option.reason} onClick={() => setAmount(option.amount)} >{option.reason}</option>
                                ))
                            }
                        </Field>
                        <ErrorMessage name="credit" />
                    </div>

                    <div>
                        <Field
                            type="text"
                            name="available"
                            className="form-control mt-2"
                            // value={amount.amount}
                            disabled
                        />
                    </div>

                    <div>
                        <Field
                            type="text"
                            name="reason"
                            className="form-control mt-2"
                            placeholder="Enter Reason for Debit"
                        />
                        <ErrorMessage name="reason" />
                    </div>

                    <div>
                        <Field
                            type="number"
                            name="amount"
                            className="form-control mt-2"
                            placeholder="Enter amount to Debit"
                        />
                        <ErrorMessage name="amount" />
                    </div>
                    <div className="w3-center">
                        <button type="submit" className="btn w3-red mt-2" >Debit</button>
                    </div>
                </Form>

            </Formik>
        </div>
    )
}

export default NewDebit
