import React,{useState, useEffect} from 'react'
import { Formik, Form, Field, ErrorMessage } from 'formik';
import * as yup from 'yup'
import Axios from 'axios';

function Credit() {

    const [formData, setformData] = useState({})
    const [count,setCount] = useState(0)
    console.log(formData)

    useEffect(() => {
        console.log("useEffect")
        Axios.post("http://localhost:2000/credit",formData)
        .then((res)=>{
            console.log(res.data)
        })
        .catch(err=>{
            console.log(err)
        })
    },[count,formData])

    const initialValues = {
        reason:"",
        name:"",
        amount:""
    }

    const submit = values => {
        console.log(values)
        setformData(values)        
    }

    const validattionSchema = yup.object({
        reason:yup.string().required("Required reason"),
        name: yup.string().required("Required"),
        amount:yup.string().required("Required")

    })
    return (
        <div>
            <Formik
                initialValues={initialValues}
                onSubmit={submit}
                validationSchema={validattionSchema}
            >
                <Form>
                    <div>
                        <Field type="text" name="reason" className="form-control mt-3" placeholder="Enter Amount Description" />
                        <ErrorMessage name="reason" />
                    </div>
                    <div>
                        <Field type="number" name="amount" className="form-control mt-3" placeholder="Enter Amount To Credite" />
                        <ErrorMessage name="amount" />
                    </div>
                    <div>
                        <Field type="text" name="name" className="form-control mt-3" placeholder="Enter your name" />
                        <ErrorMessage name="name" />
                    </div>
                    <div className="w3-center">
                        <button type="submit" onClick={()=>setCount(count+1)} className="btn w3-brown mt-3">Submit</button>   
                    </div>                   
                </Form>

            </Formik>
        </div>
    )
}

export default Credit
