import React,{useState} from 'react'
import { Formik, Form, Field, ErrorMessage } from 'formik';
import * as yup from 'yup';

function ExitsDebit() {

    const [reason, setReason] = useState("")

    const initialValue = {
        mainselect: "",
        subselect: "",
        reason: "",
        amount: ""
    }
    const submit = values => {
        console.log(values)
    }
    const validationSchema = yup.object({
        mainselect: yup.string().required("Plese Select Reason"),
        subselect: yup.string().required("Plese Select Reason"),
        reason: yup.string().required("Reason Required"),
        amount: yup.string().required("Amount Required")
    })

    const mainselect = [
        {
            from: "mjs"
        },
        {
            from: "Dinesh"
        },
        {
            from: "Appu"
        }
    ]

    const subselect = [
        {
            from: "mjs"
        },
        {
            from: "Dinesh"
        },
        {
            from: "Appu"
        }
    ]

    return (
        <div>
            <Formik
                initialValues={initialValue}
                onSubmit={submit}
                validationSchema={validationSchema}
            >
                <Form>
                    <div>
                        <Field
                            as="select"
                            name="mainselect"
                            className="form-control mt-2"
                            placeholder="Enter Reason for Debit"
                            onClick={(e) => setReason(e.target.value)}                                                                                                   
                        >
                            <option value="">Select From</option>
                            {
                                mainselect.map((option, index) => (
                                    <option key={index} value={option.from} >{option.from}</option>
                                ))
                            }
                        </Field>
                        <ErrorMessage name="mainselect" />
                    </div>

                    <div>
                        <Field
                            as="select"
                            name="subselect"
                            className="form-control mt-2"
                            placeholder="Enter Reason for Debit"
                            onClick={(e) => setReason(e.target.value)}                                                                                                   
                        >
                            <option value="">Select From</option>
                            {
                                subselect.map((option, index) => (
                                    <option key={index} value={option.from} >{option.from}</option>
                                ))
                            }
                        </Field>
                        <ErrorMessage name="subselect" />
                    </div>

                    <div>
                        <Field
                            type="text"
                            name="available"
                            className="form-control mt-2"
                            value={reason}
                            disabled
                        />
                    </div>

                    <div>
                        <Field
                            type="text"
                            name="reason"
                            className="form-control mt-2"
                            placeholder="Enter Reason for Debit"
                        />
                        <ErrorMessage name="reason" />
                    </div>

                    <div>
                        <Field
                            type="number"
                            name="amount"
                            className="form-control mt-2"
                            placeholder="Enter amount to Debit"
                        />
                        <ErrorMessage name="amount" />
                    </div>
                    <div className="w3-center">
                        <button type="submit" className="btn w3-red mt-2">Debit</button>
                    </div>
                </Form>

            </Formik>
        </div>
    )
}

export default ExitsDebit
