import React from 'react';
import {Formik,Form,Filed,ErrorMessage, Field} from 'formik';
import * as yup from 'yup';


function Debit() {
    const initialValue={

    }
    const submit = values =>{
        console.log(values)
    }
    const validationSchema=yup.object({
        reason: yup.string().required("Reason Required"),
        amount: yup.string().required("Amount Required")
    })
    return (
        <div className="container">
            <Formik
                initialValues={initialValue}
                onSubmit={submit}
                validationSchema={validationSchema}
            >
                <Form>
                    <div>
                        <Field type="text" name="reason" className="form-control" />
                        <ErrorMessage name="reason" />
                    </div>
                    <div>
                        <Field type="text" name="amount" className="form-control" />
                        <ErrorMessage name="amount" />
                    </div>
                    <div className="w3-center">
                        <button className="w3-button w3-red">Debit</button>
                    </div>

                </Form>

            </Formik>
        </div>
    )
}

export default Debit
