import React, { useState, useEffect } from 'react'
import { Formik, Form, Field, ErrorMessage } from 'formik';
import * as yup from 'yup';
import Axios from 'axios';

function ExitsDebit() {

    const initial = {
        mainselect: "",
        subselect: ""
    }
    const [reason, setReason] = useState(initial)
    const [mainselect, setmainSelect] = useState([])
    const [subselect, setsubSelect] = useState([])
    const [amount, setAmount] = useState()
    const [formData, setformData] = useState()



    useEffect(() => {
        Axios.get("http://localhost:2000/debit")
            .then(res => {
                setmainSelect(res.data)
            })
            .catch(err => {
                console.log(err)
            })
    }, [])

    useEffect(() => {
        Axios.get("http://localhost:2000/debit/reason", { params: { reason: reason.mainselect } })
            .then(res => {
                console.log(res.date)
                setsubSelect(res.data)
            })
            .catch(err => {
                console.log(err)
            })
    }, [reason.mainselect])

    useEffect(() => {
        Axios.get("http://localhost:2000/debit/amount", { params: { reason: reason.subselect } })
            .then(res => {
                console.log("amount", res.date)
                setAmount(res.data.amount)
            })
            .catch(err => {
                console.log(err)
            })
    }, [reason.subselect])

    useEffect(() => {
        if (formData) {
            Axios.post("http://localhost:2000/debit/exits", formData)
                .then(res => {
                    console.log(res.data)                    
                })
                .catch(err => {
                    console.log(err)
                })
        }

    }, [formData])

    const initialValue = {
        mainselect: "",
        subselect: "",
        reason: "",
        amount: "",
        available: ""
    }
    const submit = values => {
        console.log(values)
    }
    const validationSchema = yup.object({
        mainselect: yup.string().required("Plese Select Reason"),
        subselect: yup.string().required("Plese Select Reason"),
        reason: yup.string().required("Reason Required"),
        amount: yup.string().required("Amount Required")
    })

    return (
        <div>
            <Formik
                initialValues={initialValue}
                onSubmit={submit}
                validationSchema={validationSchema}
            >
                <Form>
                    <div>
                        <Field
                            as="select"
                            name="mainselect"
                            className="form-control mt-2"
                            placeholder="Enter Reason for Debit"
                            onClick={(e) => setReason({ ...reason, mainselect: e.target.value })}
                        >
                            <option value="">Select From</option>
                            {
                                mainselect.map((option, index) => (
                                    <option key={index} value={option.credit} >{option.credit}</option>
                                ))
                            }
                        </Field>
                        <ErrorMessage name="mainselect" />
                    </div>

                    <div>
                        <Field
                            as="select"
                            name="subselect"
                            className="form-control mt-2"
                            placeholder="Enter Reason for Debit"
                            onClick={(e) => setReason({ ...reason, subselect: e.target.value })}
                        >
                            <option value="">Select From</option>
                            {
                                subselect.map((option, index) => (
                                    <option key={index} value={option.reason} >{option.reason}</option>
                                ))
                            }
                        </Field>
                        <ErrorMessage name="subselect" />
                    </div>

                    <div>
                        <Field
                            type="text"
                            name="available"
                            className="form-control mt-2"
                            value={amount}
                            disabled
                        />
                    </div>

                    <div>
                        <Field
                            type="text"
                            name="reason"
                            className="form-control mt-2"
                            placeholder="Enter Reason for Debit"
                        />
                        <ErrorMessage name="reason" />
                    </div>

                    <div>
                        <Field
                            type="number"
                            name="amount"
                            className="form-control mt-2"
                            placeholder="Enter amount to Debit"
                        />
                        <ErrorMessage name="amount" />
                    </div>
                    <div className="w3-center">
                        <button type="submit" className="btn w3-red mt-2">Debit</button>
                    </div>
                </Form>

            </Formik>
        </div>
    )
}

export default ExitsDebit
