import React from 'react';
import {Formik,Form,Field,ErrorMessage} from 'formik';
import * as yup from 'yup';


function Debit() {
    const initialValue={
        select:"",
        reason:"",
        amount:""
    }
    const submit = values =>{
        console.log(values)
    }
    const validationSchema=yup.object({
        select: yup.string().required("Plese Select Reason"), 
        reason: yup.string().required("Reason Required"),
        amount: yup.string().required("Amount Required")
    })

    const select = [
        {
            from : "mjs"
        },
        {
            from : "Dinesh"
        },
        {
            from : "Appu"
        }
    ]
    return (
        <div className="container">
            <Formik
                initialValues={initialValue}
                onSubmit={submit}
                validationSchema={validationSchema}
            >
                <Form>
                    <div>
                        <Field
                            as="select"
                            name="select"
                            className="form-control mt-2"
                            placeholder="Enter Reason for Debit"
                        >
                            <option value="">Select From</option>
                            {
                                select.map((option, index) => (
                                    <option key={index} value={option.form}>{option.from}</option>
                                ))
                            }
                        </Field>
                        <ErrorMessage name="select" />
                    </div>
                    <div>
                        <Field
                            type="text"
                            name="reason"
                            className="form-control mt-2"
                            placeholder="Enter Reason for Debit"
                        />
                        <ErrorMessage name="reason" />
                    </div>

                    <div>
                        <Field
                            type="text"
                            name="available"
                            className="form-control mt-2"                            
                        />                        
                    </div>

                    <div>
                        <Field
                            type="number"
                            name="amount"
                            className="form-control mt-2"
                            placeholder="Enter amount to Debit"
                        />
                        <ErrorMessage name="amount" />
                    </div>
                    <div className="w3-center">
                        <button type="submit" className="btn w3-red mt-2">Debit</button>
                    </div>

                </Form>

            </Formik>
        </div>
    )
}

export default Debit
