import React from 'react'
import { Formik, Form, Field, ErrorMessage } from 'formik';
import * as yup from 'yup'
import Axios from 'axios';

function Credit() {

    const initialValues = {
        name:"",
        amount:""
    }

    const submit = values => {
        console.log(values)

        useEffect(() => {
            Axios.post("http://localhost:2000/credit",values)
            .then((res)=>{
                console.log(res.data)
            })
            .catch(err=>{
                console.log(err)
            })
        })
    }

    const validattionSchema = yup.object({
        name: yup.string().required("Required"),
        amount:yup.string().required("Required")
    })
    return (
        <div>
            <Formik
                initialValues={initialValues}
                onSubmit={submit}
                validationSchema={validattionSchema}
            >
                <Form>
                    <div>
                        <Field type="text" name="name" className="form-control mt-3" placeholder="Enter Amount Description" />
                        <ErrorMessage name="name" />
                    </div>
                    <div>
                        <Field type="number" name="amount" className="form-control mt-3" placeholder="Enter Amount To Credite" />
                        <ErrorMessage name="amount" />
                    </div>
                    <div className="w3-center">
                        <button type="submit" className="btn w3-brown mt-3">Submit</button>   
                    </div>                   
                </Form>

            </Formik>
        </div>
    )
}

export default Credit
